<?phpnamespace VGPM\Api;class Base {	private $_request = [];	public function __construct() {	}	public function setRequest($data) {		$this->_request = $data;	}	public function postParam($key) {		$ret = filter_input(INPUT_POST, $key);		return $ret;	}	public function getParam($key) {		$ret = filter_input(INPUT_GET, $key);		return $ret;	}	public function outMsg($code = 0, $msg = 'ok') {		header('Content-Type:application/json;charset=UTF-8');		echo json_encode(['code' => $code, 'msg' => $msg], JSON_UNESCAPED_UNICODE);		exit;	}	public function out($data = []) {		header('Content-Type:application/json;charset=UTF-8');		echo json_encode($data, JSON_UNESCAPED_UNICODE);		exit;	}	public function outGrid($total, $rows) {		header('Content-Type:application/json;charset=UTF-8');		$ret = [			'Rows'  => $rows,			'Total' => $total,		];		echo json_encode($ret, JSON_UNESCAPED_UNICODE);		exit;	}}